        /// <summary>
        /// 返回拼音码
        /// </summary>
        /// <param name="hzStr">汉字字符串</param>
        /// <returns></returns>
        public static string GetPYCode(string hzStr)
        {
            hzStr = hzStr.Replace(" ", "").Trim();
            StringBuilder result = new StringBuilder();
            string str = "";
            for (int i = 0; i < hzStr.Length; i++)
            {
                str = hzStr[i].ToString();
                if (!char.IsPunctuation(hzStr[i]))
                {
                    if (str.CompareTo("吖") < 0) { }
                    else if (str.CompareTo("驁") <= 0) str = "a";
                    else if (str.CompareTo("簿") <= 0) str = "b";
                    else if (str.CompareTo("錯") <= 0) str = "c";
                    else if (str.CompareTo("鵽") <= 0) str = "d";
                    else if (str.CompareTo("樲") <= 0) str = "e";
                    else if (str.CompareTo("鰒") <= 0) str = "f";
                    else if (str.CompareTo("腂") <= 0) str = "g";
                    else if (str.CompareTo("夻") <= 0) str = "h";
                    else if (str.CompareTo("攈") <= 0) str = "j";
                    else if (str.CompareTo("穒") <= 0) str = "k";
                    else if (str.CompareTo("鱳") <= 0) str = "l";
                    else if (str.CompareTo("旀") <= 0) str = "m";
                    else if (str.CompareTo("桛") <= 0) str = "n";
                    else if (str.CompareTo("漚") <= 0) str = "o";
                    else if (str.CompareTo("曝") <= 0) str = "p";
                    else if (str.CompareTo("囕") <= 0) str = "q";
                    else if (str.CompareTo("鶸") <= 0) str = "r";
                    else if (str.CompareTo("蜶") <= 0) str = "s";
                    else if (str.CompareTo("籜") <= 0) str = "t";
                    else if (str.CompareTo("鶩") <= 0) str = "w";
                    else if (str.CompareTo("鑂") <= 0) str = "x";
                    else if (str.CompareTo("韻") <= 0) str = "y";
                    else if (str.CompareTo("咗") <= 0) str = "z";
                    result.Append(str);
                }
                else
                    str = "";
            }
            return result.ToString();
        }
